$(function() {
    $('#a3').hover(function() {
        $('#af3').css('z-index', '99');
        $('#af3').stop(true,false).animate({opacity:1.0},500)
    }, function() {
        // on mouseout, reset the background colour
        $('#af3').stop(true,false).animate({opacity:0.0},200, function() {
            $('#af3').css('z-index', '-1');
        })
    });
});
